import { WidgetImageStore } from 'capacitor-widget-image-store';

window.testEcho = async () => {
  const base64Data = "data:image/png;base64,"; // Beispiel-Daten oder reale Daten
  const appGroup = "group.de.kisimedia.WidgetImageStore"; // dein AppGroup-Identifier

  try {
    const result = await WidgetImageStore.save({
      base64: base64Data,
      filename: "testwidget.jpg",
      appGroup: appGroup,
    });
    alert("Image saved to: " + result.path);
  } catch (err) {
    alert("Fehler: " + err.message);
  }
};